<?php

namespace App\Controller;

use App\Entity\User;
use App\Repository\UserRepository;
use Symfony\Bundle\FrameworkBundle\Controller\AbstractController;
use Symfony\Component\HttpFoundation\Response;

class UserListController extends AbstractController
{
    public function index(UserRepository $userRepository): Response
    {
        $users = $userRepository->findBy([], ['id' => 'ASC']);

        return $this->render('user_list/index.html.twig', ['users' => $users]);
    }

    public function avatarSrc(User $user): Response
    {
        if ($user->getAvatar()) {
            return new Response("data:image/png;base64, {$user->getAvatarBase64()}");
        } else {
            return new Response('data:image/png;base64,');
        }
    }

    public function avatarSrcByName(string $username, UserRepository $userRepository): Response
    {
        $user = $userRepository->findOneBy(['username' => $username]);

        if (!$user) {
            return new Response('');
        }

        return $this->avatarSrc($user);
    }
}
